/* configure here =========================================================== */
/*  Zendesk field ID which represents the "Slack Thread ID" field.
*/
const ISSUE_KEY_FIELD_ID = 7022397804317;

/* ========================================================================== */
new_items = [];

for (item of $items("Get ticket")) {

    // instantiate a new variable for status
    var custom_fields = item.json["custom_fields"];
    var slack_thread_id = "";
    for (var i = 0; i < custom_fields.length; i++) {
        if (custom_fields[i].id == ISSUE_KEY_FIELD_ID) {
            slack_thread_id = custom_fields[i].value;
            break;
        }
    }

    // push the new item to the new_items array
    new_items.push({
        "Slack Thread ID": slack_thread_id
    });
}

return new_items;
