const max_results = $('CONFIG').first().json.max_results;
const min_traffic = $('CONFIG').first().json.min_traffic;

const gsheet = $("Get saved keywords").all();
const gsheetKeys = gsheet.map(record => record.json.trending_keyword);

const items = $('XML').first().json.rss.channel.item;
const trafficKey = Object.keys(items[0]).find(key => key.includes("approx_traffic"));
const parseTraffic = (traffic) => parseInt(traffic.replace('+', ''), 10);

const newItems = items.map(item => {
    const links = Array.isArray(item["ht:news_item"]) ? item["ht:news_item"].slice(0, 3) : [];

    const flattenedLinks = links.reduce((acc, news, index) => {
        acc[`news_item_url${index + 1}`] = news["ht:news_item_url"];
        acc[`news_item_title${index + 1}`] = news["ht:news_item_title"];
        acc[`news_item_picture${index + 1}`] = news["ht:news_item_picture"];
        acc[`news_item_source${index + 1}`] = news["ht:news_item_source"];
        return acc;
    }, {});

    return {
        trending_keyword: item.title,
        approx_traffic: parseTraffic(item[trafficKey]),
        pubDate: item.pubDate,
        ...flattenedLinks, // Aggiungi i link
    };
}).filter(item => 
    item.approx_traffic >= min_traffic && 
    !gsheetKeys.includes(item.trending_keyword) // Filtra quelli già presenti in Google Sheets
);

let sortedItems = newItems.sort((a, b) => b.approx_traffic - a.approx_traffic);
if (max_results > 0) {
    sortedItems = sortedItems.slice(0, max_results);
}

return sortedItems;
