// Get the global workflow static data
const staticData = getWorkflowStaticData('global');

// Access its data
const lastRssId = staticData.lastRssId

let list = []


for (const item of $items("RSS Feed Read")){
  let currentId = item.json["id"].split('/').pop()
  if(currentId == lastRssId) break;
  list.push({'json': {
    'id': currentId,
    'lastId': lastRssId,
    'title': item.json["title"],
    'url': item.json["link"]
  }})
}


// Get the last ID from Rss Feed
let currentRssId = $item(0).$node["RSS Feed Read"].json["id"].split('/').pop()

// TODO: make a loop to get all the items beyond the last saved id
if(!lastRssId || currentRssId != lastRssId)
{  
  // Update its data
  staticData.lastRssId = currentRssId;

}
else { list = [{'json':{'id': 'NaN', 'lastId': staticData.lastRssId }}] }
return list;
