const newItems = [];

for (let i=0;i<items.length;i++) {
  wishes_array = items[i].json.Congratulations.split(',');
  greeting = wishes_array.shift();

  new_wish = greeting + ' ' + items[i].json['First Name'] + ',' + wishes_array.join(',');
  newItems.push({json: {Wishes: new_wish, "Phone Number": items[i].json['Phone Number']}});
}

return newItems;
