// Helper function to strip HTML tags
function stripHtml(html) {
  return html
    .replace(/<[^>]+>/g, '')    // remove all HTML tags
    .replace(/&nbsp;/g, ' ')     // decode HTML entities
    .replace(/&[a-z]+;/g, '')    // remove other HTML entities
    .replace(/\s+/g, ' ')        // normalize whitespace
    .trim();
}

return items.map(item => {
  const data = item.json;

  // Flatten job_poster
  if (data.job_poster) {
    data.job_poster_name = data.job_poster.name || '';
    data.job_poster_title = data.job_poster.title || '';
    data.job_poster_url = data.job_poster.url || '';
    delete data.job_poster;
  }

  // Flatten base_salary
  if (data.base_salary) {
    data.salary_min = data.base_salary.min_amount || '';
    data.salary_max = data.base_salary.max_amount || '';
    data.salary_currency = data.base_salary.currency || '';
    data.salary_period = data.base_salary.payment_period || '';
    delete data.base_salary;
  }

  // Clean up job description HTML
  if (data.job_description_formatted) {
    data.job_description_plain = stripHtml(data.job_description_formatted);
  }

  return { json: data };
});
