/* configure here =========================================================== */
/*  Zendesk field ID which represents the "GitHub Issue Number" field.
*/
const ISSUE_KEY_FIELD_ID = 6721726848029;

/* ========================================================================== */
new_items = [];

for (item of $items("Get ticket")) {

    // instantiate a new variable for status
    var custom_fields = item.json["custom_fields"];
    var github_issue_number = "";
    for (var i = 0; i < custom_fields.length; i++) {
        if (custom_fields[i].id == ISSUE_KEY_FIELD_ID) {
            github_issue_number = custom_fields[i].value;
            break;
        }
    }

    // push the new item to the new_items array
    new_items.push({
        "GitHub Issue Number": github_issue_number
    });
}

return new_items;
