const orderJsonKeys = (jsonObj) => {
  const ordered = {};
  Object.keys(jsonObj).sort().forEach(key => {
    ordered[key] = jsonObj[key];
  });
  return ordered;
}

// Check if file returned with content
if (Object.keys($input.all()[0].json).includes("content")) {
  // Decode base64 content and parse JSON
  const origWorkflow = JSON.parse(Buffer.from($input.all()[0].json.content, 'base64').toString());
  const n8nWorkflow = $input.all()[1].json;

  // Order JSON objects
  const orderedOriginal = orderJsonKeys(origWorkflow);
  const orderedActual = orderJsonKeys(n8nWorkflow);

  // Determine difference
  if (JSON.stringify(orderedOriginal) === JSON.stringify(orderedActual)) {
    $input.all()[0].json.github_status = "same";
  } else {
    $input.all()[0].json.github_status = "different";
    $input.all()[0].json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2);
  }
  $input.all()[0].json.content_decoded = orderedOriginal;
// No file returned / new workflow
} else if (Object.keys($input.all()[0].json).includes("data")) {
  const origWorkflow = JSON.parse($input.all()[0].json.data);
  const n8nWorkflow = $input.all()[1].json;

  // Order JSON objects
  const orderedOriginal = orderJsonKeys(origWorkflow);
  const orderedActual = orderJsonKeys(n8nWorkflow);

  // Determine difference
  if (JSON.stringify(orderedOriginal) === JSON.stringify(orderedActual)) {
    $input.all()[0].json.github_status = "same";
  } else {
    $input.all()[0].json.github_status = "different";
    $input.all()[0].json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2);
  }
  $input.all()[0].json.content_decoded = orderedOriginal;

} else {
  // Order JSON object
  const n8nWorkflow = $input.all()[1].json;
  const orderedActual = orderJsonKeys(n8nWorkflow);

  // Proper formatting
  $input.all()[0].json.github_status = "new";
  $input.all()[0].json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2);
}

// Return items
return $input.all();

// Function to beautify JSON
function beautifyJson(jsonString) {
  try {
    // Parse the JSON string
    const jsonObject = JSON.parse(jsonString);

    // Format the JSON with indentation
    return jsonObject; // Return the parsed object directly
  } catch (error) {
    // Return the error message if JSON is invalid
    return `Invalid JSON: ${error.message}`;
  }
}

// Retrieve the JSON object from the input data
const input = $input.all()[0].json;

// Extract the JSON string from the stdout field
const jsonString = input.stdout.match(/\[{.*}\]/s);

// Check if a valid JSON string is found
if (!jsonString) {
  return {
    json: {
      error: "No valid JSON string found in stdout."
    }
  };
}

// Beautify the JSON
const beautifiedJson = beautifyJson(jsonString[0]);

// Output the beautified JSON, ensuring each entry is in an object with a 'json' key
const output = beautifiedJson.map(entry => ({ json: entry }));

// Return the output
return output;
