// Process Message Node
const items = $input.all();
const item = items[0];

// Get message data from the correct path
const message = item.json.body['data[PARAMS][MESSAGE]'];
const dialogId = item.json.body['data[PARAMS][DIALOG_ID]'];

const sessionId = item.json.body['data[PARAMS][CHAT_ENTITY_DATA_1]'].split("|")[5];

const botId = Object.keys(item.json.body)
  .filter(key => key.startsWith("data[BOT]") && key.endsWith("[BOT_ID]"))
  .map(key => $json.body[key])
  .shift() || null;
const userId = item.json.body['data[USER][ID]'];

// Get auth data
const auth = {
  access_token: item.json.access_token,
  domain: item.json.domain
};

if (message.toLowerCase() === "what's hot") {
  return {
    json: {
      DIALOG_ID: dialogId,
      SESSION_ID: sessionId,
      BOT_ID: botId,
      USER_ID: userId,
      MESSAGE_ORI: message,
      MESSAGE: "Hi! I am an example-bot.\nI repeat what you say",
      AUTH: auth.access_token,
      DOMAIN: auth.domain
    }
  };
} else {
  return {
    json: {
      DIALOG_ID: dialogId,
      SESSION_ID: sessionId,
      BOT_ID: botId,
      USER_ID: userId,
      MESSAGE_ORI: message,
      MESSAGE: `You said:\n${message}`,
      AUTH: auth.access_token,
      DOMAIN: auth.domain
    }
  };
}

// Process Join Node
const items = $input.all();
const item = items[0];

// Get dialog ID from the correct path
const dialogId = item.json.body['data[PARAMS][DIALOG_ID]'];

// Get auth data
const auth = {
  access_token: item.json.access_token,
  domain: item.json.domain
};

const message = 
  'ITR Menu:\n' +
  '[send=1]1. find out more about me[/send]\n' +
  '[send=0]0. wait for operator response[/send]';

return {
  json: {
    DIALOG_ID: dialogId,
    MESSAGE: message,
    AUTH: auth.access_token,
    DOMAIN: auth.domain
  }
};

// Process Install Node
const items = $input.all();
const item = items[0];

// Get the webhook URL from input
const handlerBackUrl = item.json.webhookUrl;

// Get auth data directly from item.json
const auth = {
  access_token: item.json.access_token,
  application_token: item.json.application_token,
  domain: item.json.domain
};

return {
  json: {
    handler_back_url: handlerBackUrl,
    CODE: 'OpenChanelExampleBot',
    TYPE: 'O',
    OPENLINE: 'Y',
    EVENT_MESSAGE_ADD: handlerBackUrl,
    EVENT_WELCOME_MESSAGE: handlerBackUrl,
    EVENT_BOT_DELETE: handlerBackUrl,
    PROPERTIES: {
      NAME: 'Open chanel Bot',
      LAST_NAME: 'Example',
      COLOR: 'AQUA',
      EMAIL: 'no@example.com',
      PERSONAL_BIRTHDAY: '2020-07-18',
      WORK_POSITION: 'Report on affairs',
      PERSONAL_GENDER: 'M'
    },
    // Use the auth data from item.json
    AUTH: auth.access_token,
    CLIENT_ID: auth.application_token,
    DOMAIN: auth.domain
  }
};

