parameters.jsCode:
const inputJson = $input.first().json;
const rawUrls = inputJson.competitor_urls;

const competitorUrls = rawUrls
  .split(",")
  .map(url => url.trim())
  .filter(url => url.length > 0);

const outputJson = {
  ...inputJson,
  competitorUrls: competitorUrls
};

return [{ json: outputJson }];

