return items.map(item => {
  const jsonData = JSON.stringify(item.json);
  const binaryData = Buffer.from(jsonData).toString('base64');
  item.binary = {
    data: {
      data: binaryData,
      mimeType: 'application/json',
      fileName: 'data.json'
    }
  };
  return item;
});
