// Get the input data
const items = $input.all()

// Initialize an array to hold the split items
const splitItems = [];

// Iterate over each item
items.forEach(item => {
  // Check if the item has binary data
  if (item.binary) {
    // Iterate over each binary field
    for (const [key, value] of Object.entries(item.binary)) {
      // Create a new item for each binary file
      splitItems.push({
        json: {},
        binary: {
          data: value
        }
      });
    }
  }
});

// Return the split items
return splitItems;

const items = $input.first().json.html;

// Ensure items is an array
const htmlArray = Array.isArray(items) ? items : [items];

function htmlToFormattedText(html) {
    // Replace heading tags (h1-h6) with bold text
    html = html.replace(/<h[1-6]>(.*?)<\/h[1-6]>/gi, "\n**$1**\n");

    // Replace paragraph tags with spacing
    html = html.replace(/<p>(.*?)<\/p>/gi, "\n$1\n");

    // Replace line breaks with newline characters
    html = html.replace(/<br\s*\/?>/gi, "\n");

    // Remove all other HTML tags
    html = html.replace(/<[^>]+>/g, "").trim();

    // Remove extra newlines
    return html.replace(/\n{2,}/g, "\n").trim();
}

const updatedItems = htmlArray.map((item) => {
    const htmlContent = item?.json?.html || item;
    const textContent = htmlToFormattedText(htmlContent);
    return { textContent };
});

return updatedItems;
