parameters.query:
=query PostRanking{
  posts(postedAfter:"{{new Date(new Date(Date.now()).getTime() - (1000*60*60*1*24)).toUTCString()}}", order:RANKING, first:5, postedBefore:"{{new Date(Date.now()).toUTCString()}}"){
    edges {
      node {
        name
        tagline
        description
        votesCount
        reviewsRating
      }
    }
  }
}
