let filter_method = "POST"
let last_processed = $json.value ? $json.value : 0
let data = $json.data

function dateToTime(datetime){
  return new Date(datetime.replace(" ", "T") + "Z").getTime()
}

//Convert datetimes to timestamps
data.forEach(datum=>{datum.created_at = dateToTime(datum.created_at)})

//Filter all new POST requests
return data.filter(datum=>!last_processed || datum.created_at > last_processed).filter(datum=>!filter_method || datum.method==filter_method)
