={
  "post": {
    "accountId": "{{ $('Assign Social Media IDs').item.json.youtube_id }}",
    "target": {
      "targetType": "youtube",
      "title": "{{ $('Get my video').item.json.Titre }}",
      "privacyStatus": "unlisted",
      "shouldNotifySubscribers": "false"
    },
    "content": {
      "text": "{{ $('Get my video').item.json.DESCRIPTION }}",
      "platform": "youtube",
      "mediaUrls": [
        "{{ $json.url }}"
      ]
    }
  }
}

