const columnName = "Subitems"
function getColumnValue(item, columnId) {
    const column = item.column_values.find(column => column.column.title === columnId);
    if (column) {
          return column
    } else {
        return null;
    }
}
const columnValue = getColumnValue($input.last().json, columnName);
