var date = new Date().toISOString();
var day = new Date().getDay();
const weekday = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

items[0].json.date_today = date;
items[0].json.day_today = weekday[day];

return items;

// Create our Slack message
// This will output a list of Ticket URLs with the status and the subject
// 12345 [STATUS] - Ticket Subject
let message = "*Hello , Please find below a list of your meetings for today*. \n";

// Loop the input items
for (item of items) {
  // Append the ticket information to the message
  message += "*" + item.json.Name +' @ '+ item.json.Time + "\n*  - " + item.json.URL + "\n"; 
}

// Return our message
return [{json: {message}}];
