const crypto = require('crypto');

const password = 'a random password';

const encryptedData = $input.first().json.email;


function decrypt(encrypted, password) {
 // Extract the IV and the encrypted text
 const parts = encrypted.split(':');
 const iv = Buffer.from(parts.shift(), 'hex');

 // Create a key from the password
 const key = crypto.scryptSync(password, 'salt', 32);

 // Create a decipher
 const decipher = crypto.createDecipheriv('aes-256-cbc', key, iv);

 // Decrypt the text
 let decrypted = decipher.update(parts.join(':'), 'hex', 'utf8');
 decrypted += decipher.final('utf8');

 // Return the decrypted text
 return decrypted;
}

return [
 {
 json: {
 email: decrypt(encryptedData, password),
 }
 }
];

const crypto = require('crypto');

const password = 'a random password';
const email = 'james@brown.com';


function encrypt(text, password) {
 // Generate a secure random initialization vector
 const iv = crypto.randomBytes(16);

 // Create a key from the password
 const key = crypto.scryptSync(password, 'salt', 32);

 // Create a cipher
 const cipher = crypto.createCipheriv('aes-256-cbc', key, iv);

 // Encrypt the text
 let encrypted = cipher.update(text, 'utf8', 'hex');
 encrypted += cipher.final('hex');

 // Return the IV and the encrypted text
 return `${iv.toString('hex')}:${encrypted}`;
}

return [
 {
 json: {
 email: encrypt(email, password),
 }
 }
];

