parameters.jsCode:
const items = [];

for (const item of $input.all()) {
 const body = item.json.body;
 if (!body) continue;

 // Simple split approach
 const sections = body.split('\n\n');
 const title = sections[0].replace(/\*\*/g, '');
 const participants = sections[1].split('\n').slice(1).join('\n');
 const summary = sections[2].split('\n').slice(1).join('\n');
 const tasks = sections[3].split('\n').slice(1).join('\n');
 const dates = sections[4].split('\n').slice(1).join('\n');

 const html = `<html>
<body style="font-family: Arial, sans-serif; max-width: 800px; margin: 20px;">
<h1 style="color: #2c3e50; border-bottom: 2px solid #3498db; padding-bottom: 10px;">${title}</h1>
<h2 style="color: #2c3e50; margin-top: 20px;">Participants:</h2>
<ul style="list-style-type: none; padding-left: 20px;">
${participants.split('\n').map(p => `<li>${p.replace('- ', '')}</li>`).join('\n')}
</ul>
<h2 style="color: #2c3e50; margin-top: 20px;">Meeting Summary:</h2>
<p style="margin-left: 20px;">${summary}</p>
<h2 style="color: #2c3e50; margin-top: 20px;">Tasks:</h2>
<ul style="margin-left: 20px;">
${tasks.split('\n').map(t => `<li>${t.replace('- ', '')}</li>`).join('\n')}
</ul>
<h2 style="color: #2c3e50; margin-top: 20px;">Important Dates:</h2>
<ul style="margin-left: 20px;">
${dates.split('\n').map(d => `<li>${d.replace('- ', '')}</li>`).join('\n')}
</ul>
</body>
</html>`;

 items.push({
 json: {
 html,
 to: item.json.to,
 subject: item.json.subject
 }
 });
}

return items;

