// Get the HTML content from the previous node
const htmlContent = $input.first().json.text;

// Use regex to extract the text between the first h1 tags
const titleRegex = /<h1>(.*?)<\/h1>/s;
const match = htmlContent.match(titleRegex);

// Extract the title or set to empty string if not found
const title = match ? match[1] : '';

// Return the extracted title
return [{
  json: {
    title: title,
    content: htmlContent
  }
}];
