// Function: Compute waitSeconds
const wordCount = $json.message.split(' ').filter(w=>w).length;
return [{ json: { 
  context_id: $json.context_id,
  message: $json.message,
  waitSeconds: wordCount < 5 ? 45 : 30 
}}];

