={{
 $json.text
 .substring($json.text.search(/\nSec\.\nA[0-9]{1,4}\.[0-9]{1,5}\.AA/), $json.text.length)
 .split(/\nSec\.\nA[0-9]{1,2}\.[0-9]{1,2}\.AA/g)
 .filter(text => !text.isEmpty())
 .map(text => {
 const output = text.replaceAll('AA', ' ').replaceAll('\nA', ' ');
 const title = output.substring(0, output.indexOf('.'));
 const content = output.substring(output.indexOf('.')+1, output.length).replaceAll('\n', ' ').trim();
 return { title, content };
 })
}}

={{
 $json.labels.map((label, idx) => ({
 label: label.label.match(/\d.+/)[0].replace(/\.$/, ''),
 title: $json.contents[idx].title,
 content: $json.contents[idx].content,
 chapter: $('Extract PDF Contents').first().json.info.Title,
 }))
}}

