parameters.content:
### Enrich new Discourse members then notify in Slack for high value leads
### [🎥 Watch set up video (~2min)]({{ $env.WEBHOOK_URL }}
![Example result in Slack]({{ $env.WEBHOOK_URL }}

parameters.blocksUi:
={
	"blocks": [
		{
			"type": "section",
			"text": {
				"type": "mrkdwn",
				"text": "A high value lead just signed up on our Discourse community 👇"
			}
		},
		{
			"type": "context",
			"elements": [
				{
					"type": "image",
					"image_url": "{{ $('Enrich user with Clearbit').item.json["avatar"] }}",
					"alt_text": "User's profile avatar"
				},
				{
					"type": "mrkdwn",
					"text": "*{{ $('Enrich user with Clearbit').item.json["name"]["fullName"] }}*, *{{ $('Enrich user with Clearbit').item.json["employment"]["title"] }}* at *{{ $('Enrich user with Clearbit').item.json["employment"]["name"] }}* ({{ $json["category"]["industry"] }})"
				}
			]
		},
		{
			"type": "divider"
		},
		{
			"type": "actions",
			"block_id": "actionblock789",
			"elements": [
				{
					"type": "button",
					"text": {
						"type": "plain_text",
						"text": "Open LinkedIn Profile"
					},
					"style": "primary",
					"url": "{{ $env.WEBHOOK_URL }}{{ $('Enrich user with Clearbit').item.json["linkedin"]["handle"]}}"
				},
				{
					"type": "button",
					"text": {
						"type": "plain_text",
						"text": "Email {{ $('Enrich user with Clearbit').item.json["name"]["givenName"] }} "
					},
					"url": "mailto:{{ $('On new Discourse user').item.json["body"]["user"]["email"] }}"
				}
			]
		}
	]
}
